import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';
import './index.css';

ReactDOM.createRoot(document.getElementById('root')).render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

import React from 'react';
import Hero from './components/Hero';
import Features from './components/Features';
import Testimonials from './components/Testimonials';
import Screenshots from './components/Screenshots';
import BottomCTA from './components/BottomCTA';
import Footer from './components/Footer';

const App = () => {
  return (
    <div className="min-h-screen bg-gray-50">
      <Hero />
      <Screenshots />
      <Testimonials />
      <Features />
      <BottomCTA />
      <Footer />
    </div>
  );
};

export default App;

import React from 'react';

const Hero = () => {
  return (
    <div className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white py-20 px-4">
      <div className="max-w-6xl mx-auto text-center">
        <h1 className="text-4xl md:text-6xl font-bold mb-6">Your App's Journey Starts Here</h1>
        <p className="text-xl mb-8 max-w-2xl mx-auto">Experience the future of mobile innovation with our revolutionary app.</p>
        <div className="flex flex-col sm:flex-row justify-center gap-4">
          <button className="bg-black text-white px-8 py-3 rounded-lg hover:bg-gray-800 transition">
            Download for iOS
          </button>
          <button className="bg-white text-black px-8 py-3 rounded-lg hover:bg-gray-100 transition">
            Download for Android
          </button>
        </div>
      </div>
    </div>
  );
};

export default Hero;

import React, { useState } from 'react';

const Screenshots = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const screenshots = [
    'Screen 1', 'Screen 2', 'Screen 3', 'Screen 4'
  ];

  return (
    <div className="py-20 px-4 bg-white">
      <div className="max-w-6xl mx-auto">
        <h2 className="text-3xl font-bold text-center mb-12">App Screenshots</h2>
        <div className="relative">
          <div className="flex justify-center items-center">
            <button onClick={() => setCurrentSlide((prev) => (prev === 0 ? screenshots.length - 1 : prev - 1))} className="p-2">
              ←
            </button>
            <div className="mx-4 text-center">{screenshots[currentSlide]}</div>
            <button onClick={() => setCurrentSlide((prev) => (prev === screenshots.length - 1 ? 0 : prev + 1))} className="p-2">
              →
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Screenshots;

import React from 'react';

const Testimonials = () => {
  const testimonials = [
    { name: 'John Doe', quote: 'Amazing app that changed my life!', role: 'User' },
    { name: 'Jane Smith', quote: 'Best app I've ever used!', role: 'User' },
    { name: 'Mike Johnson', quote: 'Incredibly intuitive and helpful!', role: 'User' }
  ];

  return (
    <div className="py-20 px-4 bg-gray-100">
      <div className="max-w-6xl mx-auto">
        <h2 className="text-3xl font-bold text-center mb-12">What Our Users Say</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {testimonials.map((testimonial, index) => (
            <div key={index} className="bg-white p-6 rounded-lg shadow-md">
              <p className="text-gray-600 mb-4">"{testimonial.quote}"</p>
              <p className="font-bold">{testimonial.name}</p>
              <p className="text-gray-500">{testimonial.role}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Testimonials;

import React from 'react';

const Features = () => {
  const features = [
    { title: 'Easy to Use', description: 'Intuitive interface for the best experience' },
    { title: 'Fast Performance', description: 'Lightning-fast response times' },
    { title: 'Secure', description: 'Your data is always protected' },
    { title: '24/7 Support', description: 'We're here to help anytime' }
  ];

  return (
    <div className="py-20 px-4 bg-white">
      <div className="max-w-6xl mx-auto">
        <h2 className="text-3xl font-bold text-center mb-12">Key Features</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {features.map((feature, index) => (
            <div key={index} className="text-center p-6">
              <h3 className="text-xl font-bold mb-2">{feature.title}</h3>
              <p className="text-gray-600">{feature.description}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Features;

import React from 'react';

const BottomCTA = () => {
  return (
    <div className="bg-blue-600 text-white py-20 px-4">
      <div className="max-w-6xl mx-auto text-center">
        <h2 className="text-3xl font-bold mb-6">Ready to Get Started?</h2>
        <p className="text-xl mb-8">Download now and join millions of satisfied users!</p>
        <div className="flex flex-col sm:flex-row justify-center gap-4">
          <button className="bg-black text-white px-8 py-3 rounded-lg hover:bg-gray-800 transition">
            Download for iOS
          </button>
          <button className="bg-white text-black px-8 py-3 rounded-lg hover:bg-gray-100 transition">
            Download for Android
          </button>
        </div>
      </div>
    </div>
  );
};

export default BottomCTA;
