import React from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import Features from './components/Features';
import Testimonials from './components/Testimonials';
import Carousel from './components/Carousel';
import CallToAction from './components/CallToAction';
import FAQ from './components/FAQ';

const App = () => {
  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      <Hero />
      <Carousel />
      <Features />
      <Testimonials />
      <FAQ />
      <CallToAction />
    </div>
  );
};

export default App;

import React from 'react';

const Header = () => {
  return (
    <header className="fixed w-full bg-white shadow-md z-50">
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        <div className="text-2xl font-bold text-blue-600">AppName</div>
        <nav className="hidden md:flex space-x-6">
          <a href="#features" className="hover:text-blue-600">Features</a>
          <a href="#testimonials" className="hover:text-blue-600">Testimonials</a>
          <a href="#faq" className="hover:text-blue-600">FAQ</a>
        </nav>
        <button className="bg-blue-600 text-white px-6 py-2 rounded-full hover:bg-blue-700">Download</button>
      </div>
    </header>
  );
};

export default Header;

import React from 'react';

const Hero = () => {
  return (
    <section className="pt-24 pb-12 px-4 text-center bg-gradient-to-r from-blue-500 to-purple-600 text-white">
      <div className="container mx-auto">
        <h1 className="text-4xl md:text-6xl font-bold mb-6">Transform Your Mobile Experience</h1>
        <p className="text-xl mb-8 max-w-2xl mx-auto">Experience the future of mobile apps with our innovative solution</p>
        <div className="flex justify-center space-x-4">
          <button className="bg-white text-blue-600 px-8 py-3 rounded-full hover:bg-gray-100">iOS Download</button>
          <button className="bg-white text-blue-600 px-8 py-3 rounded-full hover:bg-gray-100">Android Download</button>
        </div>
      </div>
    </section>
  );
};

export default Hero;

import React from 'react';

const Features = () => {
  const features = [
    { title: 'Easy to Use', description: 'Intuitive interface that anyone can master' },
    { title: 'Fast & Reliable', description: 'Lightning-fast performance you can count on' },
    { title: 'Secure', description: 'Your data is protected with military-grade encryption' },
  ];

  return (
    <section id="features" className="py-16 px-4 bg-white">
      <div className="container mx-auto">
        <h2 className="text-3xl font-bold text-center mb-12">Key Features</h2>
        <div className="grid md:grid-cols-3 gap-8">
          {features.map((feature, index) => (
            <div key={index} className="p-6 bg-gray-50 rounded-lg text-center">
              <h3 className="text-xl font-semibold mb-4">{feature.title}</h3>
              <p>{feature.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Features;

import React from 'react';

const Testimonials = () => {
  const testimonials = [
    { name: 'John Doe', comment: 'Best app I\'ve ever used!', rating: 5 },
    { name: 'Jane Smith', comment: 'Completely changed how I work', rating: 5 },
    { name: 'Mike Johnson', comment: 'Highly recommended!', rating: 4 },
  ];

  return (
    <section id="testimonials" className="py-16 px-4 bg-gray-100">
      <div className="container mx-auto">
        <h2 className="text-3xl font-bold text-center mb-12">What Users Say</h2>
        <div className="grid md:grid-cols-3 gap-8">
          {testimonials.map((testimonial, index) => (
            <div key={index} className="p-6 bg-white rounded-lg shadow-md">
              <p className="mb-4">"{testimonial.comment}"</p>
              <div className="flex items-center">
                <div className="w-12 h-12 bg-gray-300 rounded-full mr-4"></div>
                <div>
                  <p className="font-semibold">{testimonial.name}</p>
                  <div className="text-yellow-400">{'★'.repeat(testimonial.rating)}</div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Testimonials;

import React, { useState } from 'react';

const Carousel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const slides = ['Screenshot 1', 'Screenshot 2', 'Screenshot 3'];

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  return (
    <section className="py-16 px-4 bg-gray-50">
      <div className="container mx-auto relative">
        <div className="max-w-2xl mx-auto bg-white p-4 rounded-lg shadow-lg">
          <div className="relative h-64 bg-gray-200 rounded">
            <div className="absolute inset-0 flex items-center justify-center">
              <p className="text-xl">{slides[currentSlide]}</p>
            </div>
            <button onClick={prevSlide} className="absolute left-0 top-1/2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-r">
              ←
            </button>
            <button onClick={nextSlide} className="absolute right-0 top-1/2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-l">
              →
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Carousel;

import React from 'react';

const CallToAction = () => {
  return (
    <section className="py-16 px-4 bg-blue-600 text-white">
      <div className="container mx-auto text-center">
        <h2 className="text-3xl font-bold mb-8">Ready to Get Started?</h2>
        <div className="flex justify-center space-x-4">
          <button className="bg-white text-blue-600 px-8 py-3 rounded-full hover:bg-gray-100">Download for iOS</button>
          <button className="bg-white text-blue-600 px-8 py-3 rounded-full hover:bg-gray-100">Download for Android</button>
        </div>
      </div>
    </section>
  );
};

export default CallToAction;

import React from 'react';

const FAQ = () => {
  const faqs = [
    { question: 'How do I get started?', answer: 'Simply download the app from your preferred app store and create an account.' },
    { question: 'Is it free?', answer: 'Yes, the basic version is free with optional premium features.' },
    { question: 'What devices are supported?', answer: 'Our app works on both iOS and Android devices.' }
  ];

  return (
    <section id="faq" className="py-16 px-4 bg-white">
      <div className="container mx-auto">
        <h2 className="text-3xl font-bold text-center mb-12">Frequently Asked Questions</h2>
        <div className="max-w-3xl mx-auto">
          {faqs.map((faq, index) => (
            <div key={index} className="mb-6 p-6 bg-gray-50 rounded-lg">
              <h3 className="text-xl font-semibold mb-2">{faq.question}</h3>
              <p className="text-gray-600">{faq.answer}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default FAQ;
