import React from 'react';
import ReactDOM from 'react-dom';
import App from './App';
import { BrowserRouter } from 'react-router-dom';

ReactDOM.render(
  <BrowserRouter>
    <App />
  </BrowserRouter>,
  document.getElementById('root')
);

import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import About from './pages/About';
import BlogPost from './pages/BlogPost';

const App = () => {
  return (
    <div className='min-h-screen bg-gray-50'>
      <Navbar />
      <Routes>
        <Route path='/' element={<Home />} />
        <Route path='/about' element={<About />} />
        <Route path='/post/:id' element={<BlogPost />} />
      </Routes>
    </div>
  );
};

export default App;

import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [searchQuery, setSearchQuery] = useState('');

  return (
    <nav className='bg-white shadow-lg'>
      <div className='max-w-6xl mx-auto px-4'>
        <div className='flex justify-between items-center py-4'>
          <Link to='/' className='font-bold text-xl'>Blog</Link>
          <div className='flex items-center space-x-4'>
            <input
              type='text'
              placeholder='Search...'
              className='px-4 py-2 border rounded-lg'
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <Link to='/' className='hover:text-gray-700'>Home</Link>
            <Link to='/about' className='hover:text-gray-700'>About</Link>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;

import React from 'react';
import { Link } from 'react-router-dom';

const BlogCard = ({ post }) => {
  return (
    <div className='bg-white rounded-lg shadow-md overflow-hidden'>
      <img src={post.image} alt={post.title} className='w-full h-48 object-cover' />
      <div className='p-6'>
        <h2 className='text-xl font-bold mb-2'>{post.title}</h2>
        <p className='text-gray-600 mb-4'>{post.excerpt}</p>
        <Link to={`/post/${post.id}`} className='text-blue-500 hover:text-blue-700'>
          Read more
        </Link>
      </div>
    </div>
  );
};

export default BlogCard;

import React from 'react';
import BlogCard from '../components/BlogCard';
import { blogPosts } from '../data/blogPosts';

const Home = () => {
  return (
    <div className='max-w-6xl mx-auto px-4 py-8'>
      <div className='grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6'>
        {blogPosts.map(post => (
          <BlogCard key={post.id} post={post} />
        ))}
      </div>
    </div>
  );
};

export default Home;

import React from 'react';

const About = () => {
  return (
    <div className='max-w-3xl mx-auto px-4 py-8'>
      <h1 className='text-3xl font-bold mb-6'>About Us</h1>
      <img src='https://images.unsplash.com/photo-1499750310107-5fef28a66643' alt='Author' className='w-full h-64 object-cover rounded-lg mb-6' />
      <p className='text-gray-700 leading-relaxed'>
        Welcome to our blog! We're passionate about sharing knowledge and insights about various topics.
        Our goal is to create engaging and informative content that helps our readers learn and grow.
      </p>
    </div>
  );
};

export default About;

import React from 'react';
import { useParams } from 'react-router-dom';
import { blogPosts } from '../data/blogPosts';

const BlogPost = () => {
  const { id } = useParams();
  const post = blogPosts.find(post => post.id === parseInt(id));

  if (!post) return <div>Post not found</div>;

  return (
    <div className='max-w-3xl mx-auto px-4 py-8'>
      <h1 className='text-4xl font-bold mb-6'>{post.title}</h1>
      <img src={post.image} alt={post.title} className='w-full h-96 object-cover rounded-lg mb-6' />
      <div className='prose max-w-none'>
        {post.content}
      </div>
    </div>
  );
};

export default BlogPost;

export const blogPosts = [
  {
    id: 1,
    title: 'Getting Started with React',
    excerpt: 'Learn the basics of React and start building web applications.',
    image: 'https://images.unsplash.com/photo-1633356122102-3fe601e05bd2',
    content: 'React is a powerful JavaScript library for building user interfaces...',
  },
  {
    id: 2,
    title: 'Web Design Trends 2024',
    excerpt: 'Discover the latest trends in web design and development.',
    image: 'https://images.unsplash.com/photo-1507238691740-187a5b1d37b8',
    content: 'The world of web design is constantly evolving...',
  },
  {
    id: 3,
    title: 'Mastering CSS Grid',
    excerpt: 'A comprehensive guide to CSS Grid layout system.',
    image: 'https://images.unsplash.com/photo-1517134191118-9d595e4c8c2b',
    content: 'CSS Grid is a powerful tool for creating complex layouts...',
  }
];
