import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import App from './App';
import './index.css';

ReactDOM.createRoot(document.getElementById('root')).render(
  <React.StrictMode>
    <BrowserRouter>
      <App />
    </BrowserRouter>
  </React.StrictMode>
);

import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './pages/Home';
import About from './pages/About';
import Courses from './pages/Courses';
import Faculty from './pages/Faculty';
import Events from './pages/Events';
import Contact from './pages/Contact';

const App = () => {
  return (
    <div className="min-h-screen flex flex-col">
      <Navbar />
      <main className="flex-grow">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/courses" element={<Courses />} />
          <Route path="/faculty" element={<Faculty />} />
          <Route path="/events" element={<Events />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>
      </main>
      <Footer />
    </div>
  );
};

export default App;

import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="bg-blue-600 text-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <Link to="/" className="font-bold text-xl">CodeTech Institute</Link>

          <div className="hidden md:block">
            <div className="flex items-center space-x-4">
              <Link to="/" className="hover:bg-blue-700 px-3 py-2 rounded">Home</Link>
              <Link to="/about" className="hover:bg-blue-700 px-3 py-2 rounded">About</Link>
              <Link to="/courses" className="hover:bg-blue-700 px-3 py-2 rounded">Courses</Link>
              <Link to="/faculty" className="hover:bg-blue-700 px-3 py-2 rounded">Faculty</Link>
              <Link to="/events" className="hover:bg-blue-700 px-3 py-2 rounded">Events</Link>
              <Link to="/contact" className="hover:bg-blue-700 px-3 py-2 rounded">Contact</Link>
            </div>
          </div>

          <button 
            className="md:hidden"
            onClick={() => setIsOpen(!isOpen)}
          >
            <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              {isOpen ? (
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              ) : (
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              )}
            </svg>
          </button>
        </div>
      </div>

      {isOpen && (
        <div className="md:hidden">
          <div className="px-2 pt-2 pb-3 space-y-1">
            <Link to="/" className="block hover:bg-blue-700 px-3 py-2 rounded">Home</Link>
            <Link to="/about" className="block hover:bg-blue-700 px-3 py-2 rounded">About</Link>
            <Link to="/courses" className="block hover:bg-blue-700 px-3 py-2 rounded">Courses</Link>
            <Link to="/faculty" className="block hover:bg-blue-700 px-3 py-2 rounded">Faculty</Link>
            <Link to="/events" className="block hover:bg-blue-700 px-3 py-2 rounded">Events</Link>
            <Link to="/contact" className="block hover:bg-blue-700 px-3 py-2 rounded">Contact</Link>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;

import React from 'react';

const Courses = () => {
  const courses = [
    {
      title: "Full Stack Web Development",
      duration: "24 weeks",
      level: "Beginner to Advanced",
      description: "Comprehensive course covering front-end and back-end development",
      topics: ["HTML/CSS", "JavaScript", "React", "Node.js", "MongoDB"]
    },
    {
      title: "Data Structures & Algorithms",
      duration: "16 weeks",
      level: "Intermediate",
      description: "Master fundamental DSA concepts and problem-solving techniques",
      topics: ["Arrays", "Linked Lists", "Trees", "Graphs", "Dynamic Programming"]
    },
    {
      title: "Mobile App Development",
      duration: "20 weeks",
      level: "Intermediate",
      description: "Learn to build cross-platform mobile applications",
      topics: ["React Native", "iOS Basics", "Android Basics", "API Integration"]
    }
  ];

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
      <h1 className="text-4xl font-bold mb-12">Our Courses</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {courses.map((course, index) => (
          <div key={index} className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-2xl font-bold mb-4">{course.title}</h2>
            <div className="mb-4">
              <p className="text-gray-600"><span className="font-semibold">Duration:</span> {course.duration}</p>
              <p className="text-gray-600"><span className="font-semibold">Level:</span> {course.level}</p>
            </div>
            <p className="text-gray-700 mb-4">{course.description}</p>
            <h3 className="font-semibold mb-2">Key Topics:</h3>
            <ul className="list-disc list-inside text-gray-700">
              {course.topics.map((topic, i) => (
                <li key={i}>{topic}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Courses;

import React from 'react';

