import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import App from './App';
import './index.css';

ReactDOM.createRoot(document.getElementById('root')).render(
  <BrowserRouter>
    <App />
  </BrowserRouter>
);

import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './pages/Home';
import Products from './pages/Products';
import About from './pages/About';
import Contact from './pages/Contact';

const App = () => {
  return (
    <div className="min-h-screen flex flex-col">
      <Navbar />
      <main className="flex-grow">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/products" element={<Products />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>
      </main>
      <Footer />
    </div>
  );
};

export default App;

import React from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  return (
    <nav className="bg-gray-900 text-white py-4">
      <div className="container mx-auto px-4 flex justify-between items-center">
        <Link to="/" className="text-2xl font-bold">Rockinox</Link>
        <div className="space-x-6">
          <Link to="/" className="hover:text-gray-300">Home</Link>
          <Link to="/products" className="hover:text-gray-300">Products</Link>
          <Link to="/about" className="hover:text-gray-300">About</Link>
          <Link to="/contact" className="hover:text-gray-300">Contact</Link>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;

import React from 'react';

const Footer = () => {
  return (
    <footer className="bg-gray-900 text-white py-6">
      <div className="container mx-auto px-4 text-center">
        <p>&copy; 2024 Rockinox. All rights reserved.</p>
      </div>
    </footer>
  );
};

export default Footer;

import React from 'react';

const Home = () => {
  return (
    <div>
      <div className="relative h-[500px]">
        <img src="https://images.unsplash.com/photo-1511379938547-c1f69419868d" alt="Music Banner" className="w-full h-full object-cover" />
        <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <h1 className="text-5xl text-white font-bold">Welcome to Rockinox</h1>
        </div>
      </div>
      <div className="container mx-auto px-4 py-12">
        <h2 className="text-3xl font-bold mb-8 text-center">Your Ultimate Music Destination</h2>
        <p className="text-lg text-center max-w-3xl mx-auto">Discover the finest collection of musical instruments and equipment at Rockinox.</p>
      </div>
    </div>
  );
};

export default Home;

import React from 'react';

const products = [
  { id: 1, name: 'Electric Guitar', image: 'https://images.unsplash.com/photo-1516924962500-2b4b3b99ea02', price: '$799' },
  { id: 2, name: 'Acoustic Drums', image: 'https://images.unsplash.com/photo-1519892300165-cb5542fb47c7', price: '$1299' },
  { id: 3, name: 'Digital Piano', image: 'https://images.unsplash.com/photo-1520523839897-bd0b52f945a0', price: '$999' },
];

const Products = () => {
  return (
    <div className="container mx-auto px-4 py-12">
      <h2 className="text-3xl font-bold mb-8">Our Products</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {products.map(product => (
          <div key={product.id} className="bg-white rounded-lg shadow-md overflow-hidden">
            <img src={product.image} alt={product.name} className="w-full h-64 object-cover" />
            <div className="p-4">
              <h3 className="text-xl font-bold">{product.name}</h3>
              <p className="text-gray-600">{product.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;

import React from 'react';

const About = () => {
  return (
    <div className="container mx-auto px-4 py-12">
      <h2 className="text-3xl font-bold mb-8">About Rockinox</h2>
      <div className="max-w-3xl mx-auto">
        <p className="text-lg mb-6">Founded in 2010, Rockinox has been serving musicians with premium quality instruments and exceptional service.</p>
        <p className="text-lg mb-6">Our mission is to inspire and enable musicians of all levels to achieve their musical dreams through access to the finest instruments and equipment.</p>
        <img src="https://images.unsplash.com/photo-1514320291840-2e0a9bf2a9ae" alt="Store" className="w-full rounded-lg mb-6" />
      </div>
    </div>
  );
};

export default About;

import React, { useState } from 'react';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Message sent successfully!');
    setFormData({ name: '', email: '', message: '' });
  };

  return (
    <div className="container mx-auto px-4 py-12">
      <h2 className="text-3xl font-bold mb-8">Contact Us</h2>
      <div className="max-w-xl mx-auto">
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block mb-2">Name</label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData({...formData, name: e.target.value})}
              className="w-full p-2 border rounded"
              required
            />
          </div>
          <div>
            <label className="block mb-2">Email</label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) => setFormData({...formData, email: e.target.value})}
              className="w-full p-2 border rounded"
              required
            />
          </div>
          <div>
            <label className="block mb-2">Message</label>
            <textarea
              value={formData.message}
              onChange={(e) => setFormData({...formData, message: e.target.value})}
              className="w-full p-2 border rounded h-32"
              required
            ></textarea>
          </div>
          <button type="submit" className="bg-gray-900 text-white px-6 py-2 rounded hover:bg-gray-700">
            Send Message
          </button>
        </form>
      </div>
    </div>
  );
};

export default Contact;
