import React from 'react'
import AlertButton from './components/AlertButton'

const App = () => {
  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <AlertButton />
    </div>
  )
}

export default App

import React from 'react'

const AlertButton = () => {
  const handleClick = () => {
    alert('Hello, you clicked the alert button!')
  }

  return (
    <button
      onClick={handleClick}
      className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-3 px-6 rounded-lg shadow-md transform transition duration-200 hover:scale-105 active:scale-95"
    >
      Click Me!
    </button>
  )
}

export default AlertButton
