import AlertButton from './components/AlertButton'

const App = () => {
  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-100 to-blue-200 flex flex-col items-center justify-center">
      <h1 className="text-3xl md:text-4xl font-bold text-blue-800 mb-8 text-center px-4">Welcome to JavaScript Alert Demo</h1>
      <AlertButton />
    </div>
  )
}

export default App

const AlertButton = () => {
  const handleClick = () => {
    alert("Thank you for clicking! This is a demonstration of JavaScript alert functionality.")
  }

  return (
    <button 
      onClick={handleClick}
      className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded-lg shadow-lg transform transition-all duration-300 hover:scale-105 active:scale-95"
    >
      Click for Alert
    </button>
  )
}

export default AlertButton
