import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';

ReactDOM.createRoot(document.getElementById('root')).render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

import React from 'react';
import AlertButton from './components/AlertButton';

const App = () => {
    return (
        <div className="min-h-screen bg-gradient-to-r from-blue-500 to-purple-600 flex items-center justify-center">
            <AlertButton />
        </div>
    );
};

export default App;

import React from 'react';

const AlertButton = () => {
    const handleClick = () => {
        alert('Hello! This is your custom alert message.');
    };

    return (
        <button 
            onClick={handleClick}
            className="px-8 py-4 bg-white text-purple-600 font-bold rounded-lg shadow-lg transform transition-all duration-300 hover:scale-105 hover:shadow-xl active:scale-95"
        >
            Click for Alert!
        </button>
    );
};

export default AlertButton;
