import AlertButton from './components/AlertButton'

const App = () => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-purple-100 flex items-center justify-center">
      <AlertButton />
    </div>
  )
}

export default App

const AlertButton = () => {
  const handleClick = () => {
    alert('Hello! You clicked the alert button!')
  }

  return (
    <button
      onClick={handleClick}
      className="px-6 py-3 bg-gradient-to-r from-blue-500 to-purple-500 text-white font-semibold rounded-lg shadow-lg hover:from-blue-600 hover:to-purple-600 transform hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-purple-400 focus:ring-opacity-50"
    >
      Click for Alert!
    </button>
  )
}

export default AlertButton
