import React from 'react'
import AlertButton from './components/AlertButton'

const App = () => {
  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-500 to-purple-600">
      <AlertButton />
    </div>
  )
}

export default App

import React from 'react'

const AlertButton = () => {
  const handleClick = () => {
    alert('Button clicked!')
  }

  return (
    <button
      onClick={handleClick}
      className="px-6 py-3 bg-white text-purple-600 font-semibold rounded-lg shadow-lg transform transition-transform duration-300 hover:scale-105 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:ring-opacity-50"
    >
      Click Me!
    </button>
  )
}

export default AlertButton
