import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';

ReactDOM.createRoot(document.getElementById('root')).render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

import React from 'react';
import AlertButton from './components/AlertButton';

const App = () => {
  return (
    <div className="w-screen h-screen flex items-center justify-center bg-gradient-to-r from-blue-500 to-purple-500">
      <AlertButton message="Hello! This is a custom alert message." />
    </div>
  );
};

export default App;

import React from 'react';

const AlertButton = ({ message }) => {
  const handleClick = () => {
    alert(message);
  };

  return (
    <button 
      onClick={handleClick}
      className="px-6 py-3 bg-white text-purple-600 rounded-full font-semibold shadow-lg transform transition-all hover:scale-105 hover:shadow-xl active:scale-95"
    >
      Click Me!
    </button>
  );
};

export default AlertButton;
