import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';
import './index.css';

ReactDOM.createRoot(document.getElementById('root')).render(<App />);

import React from 'react';
import ChatInterface from './components/ChatInterface';

const App = () => {
    return (
        <div className="min-h-screen bg-gray-100">
            <ChatInterface />
        </div>
    );
};

export default App;

import React, { useState } from 'react';
import MessageList from './MessageList';
import MessageInput from './MessageInput';

const ChatInterface = () => {
    const [messages, setMessages] = useState([]);

    const handleSendMessage = (text) => {
        const newMessage = {
            id: Date.now(),
            text,
            user: 'User',
            avatar: 'https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?w=200&h=200&fit=crop'
        };
        setMessages([...messages, newMessage]);
    };

    return (
        <div className="max-w-2xl mx-auto p-4 h-screen flex flex-col">
            <div className="bg-white rounded-lg shadow-lg p-4 flex-1 flex flex-col">
                <h1 className="text-2xl font-bold text-gray-800 mb-4">Chat Room</h1>
                <MessageList messages={messages} />
                <MessageInput onSendMessage={handleSendMessage} />
            </div>
        </div>
    );
};

export default ChatInterface;

import React from 'react';

const MessageList = ({ messages }) => {
    return (
        <div className="flex-1 overflow-y-auto mb-4">
            {messages.map((message) => (
                <div key={message.id} className="flex items-start mb-4">
                    <img
                        src={message.avatar}
                        alt={message.user}
                        className="w-8 h-8 rounded-full mr-3"
                    />
                    <div>
                        <div className="font-semibold text-gray-800">{message.user}</div>
                        <div className="bg-blue-100 rounded-lg p-3 text-gray-700">
                            {message.text}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default MessageList;

import React, { useState } from 'react';

const MessageInput = ({ onSendMessage }) => {
    const [message, setMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (message.trim()) {
            onSendMessage(message);
            setMessage('');
        }
    };

    return (
        <form onSubmit={handleSubmit} className="flex gap-2">
            <input
                type="text"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                className="flex-1 p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
                placeholder="Type your message..."
            />
            <button
                type="submit"
                className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors"
            >
                Send
            </button>
        </form>
    );
};

export default MessageInput;
