import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';
import './index.css';

ReactDOM.createRoot(document.getElementById('root')).render(<App />);

import React from 'react';
import ChatInterface from './components/ChatInterface';

const App = () => {
    return (
        <div className="min-h-screen bg-gray-100 p-4">
            <ChatInterface />
        </div>
    );
};

export default App;

import React, { useState, useRef, useEffect } from 'react';
import MessageBubble from './MessageBubble';
import InputField from './InputField';

const ChatInterface = () => {
    const [messages, setMessages] = useState([]);
    const chatEndRef = useRef(null);

    const scrollToBottom = () => {
        chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const handleSendMessage = (text) => {
        if (text.trim()) {
            setMessages([...messages, 
                { text, sender: 'user' },
                { text: 'This is a bot response', sender: 'bot' }
            ]);
        }
    };

    return (
        <div className="max-w-2xl mx-auto bg-white rounded-lg shadow-lg h-[80vh] flex flex-col">
            <div className="bg-blue-600 p-4 rounded-t-lg">
                <h1 className="text-white text-xl font-semibold">Chat Interface</h1>
            </div>
            <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {messages.map((message, index) => (
                    <MessageBubble key={index} {...message} />
                ))}
                <div ref={chatEndRef} />
            </div>
            <InputField onSend={handleSendMessage} />
        </div>
    );
};

export default ChatInterface;

import React from 'react';

const MessageBubble = ({ text, sender }) => {
    const isUser = sender === 'user';
    return (
        <div className={`flex ${isUser ? 'justify-end' : 'justify-start'}`}>
            <div className={`max-w-[70%] rounded-lg p-3 ${
                isUser ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'
            }`}>
                <p className="text-sm">{text}</p>
            </div>
        </div>
    );
};

export default MessageBubble;

import React, { useState } from 'react';

const InputField = ({ onSend }) => {
    const [message, setMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onSend(message);
        setMessage('');
    };

    return (
        <form onSubmit={handleSubmit} className="border-t p-4">
            <div className="flex gap-2">
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    className="flex-1 border rounded-lg px-4 py-2 focus:outline-none focus:border-blue-500"
                    placeholder="Type your message..."
                />
                <button
                    type="submit"
                    className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 transition-colors"
                >
                    Send
                </button>
            </div>
        </form>
    );
};

export default InputField;
