import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import App from './App';
import './index.css';

ReactDOM.createRoot(document.getElementById('root')).render(
  <BrowserRouter>
    <App />
  </BrowserRouter>
);

import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import Products from './pages/Products';
import ProductDetail from './pages/ProductDetail';
import Cart from './pages/Cart';
import Checkout from './pages/Checkout';

const App = () => {
  return (
    <div className="flex flex-col min-h-screen">
      <Header />
      <main className="flex-grow">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/products" element={<Products />} />
          <Route path="/product/:id" element={<ProductDetail />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/checkout" element={<Checkout />} />
        </Routes>
      </main>
      <Footer />
    </div>
  );
};

export default App;

import React from 'react';
import { Link } from 'react-router-dom';
import SearchBar from './SearchBar';

const Header = () => {
  return (
    <header className="bg-gray-800 text-white p-4">
      <div className="container mx-auto flex flex-col md:flex-row items-center justify-between">
        <Link to="/" className="text-2xl font-bold">CameraShop</Link>
        <nav className="mt-4 md:mt-0">
          <ul className="flex space-x-6">
            <li><Link to="/" className="hover:text-gray-300">Home</Link></li>
            <li><Link to="/products" className="hover:text-gray-300">Products</Link></li>
            <li><Link to="/cart" className="hover:text-gray-300">Cart</Link></li>
          </ul>
        </nav>
        <SearchBar />
      </div>
    </header>
  );
};

export default Header;

import React from 'react';
import NewsletterForm from './NewsletterForm';

const Footer = () => {
  return (
    <footer className="bg-gray-800 text-white py-8">
      <div className="container mx-auto grid grid-cols-1 md:grid-cols-3 gap-8 px-4">
        <div>
          <h3 className="text-xl font-bold mb-4">Contact Us</h3>
          <p>123 Camera Street</p>
          <p>Photo City, PC 12345</p>
          <p>Email: info@camerashop.com</p>
          <p>Phone: (123) 456-7890</p>
        </div>
        <div>
          <h3 className="text-xl font-bold mb-4">Quick Links</h3>
          <ul>
            <li><a href="/about" className="hover:text-gray-300">About Us</a></li>
            <li><a href="/contact" className="hover:text-gray-300">Contact</a></li>
            <li><a href="/shipping" className="hover:text-gray-300">Shipping Info</a></li>
          </ul>
        </div>
        <div>
          <h3 className="text-xl font-bold mb-4">Newsletter</h3>
          <NewsletterForm />
        </div>
      </div>
    </footer>
  );
};

export default Footer;

import React, { useState } from 'react';

const SearchBar = () => {
  const [search, setSearch] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle search logic
  };

  return (
    <form onSubmit={handleSubmit} className="mt-4 md:mt-0">
      <input
        type="text"
        placeholder="Search products..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="px-4 py-2 rounded-l text-black"
      />
      <button type="submit" className="bg-blue-500 px-4 py-2 rounded-r hover:bg-blue-600">
        Search
      </button>
    </form>
  );
};

export default SearchBar;

import React, { useState } from 'react';

const NewsletterForm = () => {
  const [email, setEmail] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle newsletter signup
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <input
        type="email"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="w-full px-4 py-2 rounded text-black"
      />
      <button type="submit" className="bg-blue-500 px-4 py-2 rounded hover:bg-blue-600 w-full">
        Subscribe
      </button>
    </form>
  );
};

export default NewsletterForm;

import React from 'react';

const Home = () => {
  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-4xl font-bold text-center mb-8">Welcome to CameraShop</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        <div className="bg-white p-6 rounded-lg shadow-md">
          <img src="https://images.unsplash.com/photo-1516035069371-29a1b244cc32" alt="Camera" className="w-full h-48 object-cover rounded mb-4" />
          <h2 className="text-xl font-bold mb-2">Featured Products</h2>
          <p>Discover our latest collection of premium cameras.</p>
        </div>
        {/* Add more featured sections */}
      </div>
    </div>
  );
};

export default Home;
