import React from 'react';
import ReactDOM from 'react-dom/client';
import { HashRouter } from 'react-router-dom';
import App from './App';
import './index.css';

ReactDOM.createRoot(document.getElementById('root')).render(
  <HashRouter>
    <App />
  </HashRouter>
);

import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import About from './pages/About';

const App = () => {
  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
      </Routes>
    </div>
  );
};

export default App;

import React from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  return (
    <nav className="bg-white shadow-md">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          <div className="flex items-center">
            <Link to="/" className="text-xl font-bold text-gray-800">John Doe</Link>
          </div>
          <div className="flex items-center">
            <Link to="/" className="px-3 py-2 text-gray-700 hover:text-gray-900">Home</Link>
            <Link to="/about" className="px-3 py-2 text-gray-700 hover:text-gray-900">About</Link>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;

import React from 'react';

const ProjectCard = ({ title, description, image, link }) => {
  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden">
      <img src={image} alt={title} className="w-full h-48 object-cover"/>
      <div className="p-4">
        <h3 className="text-xl font-semibold mb-2">{title}</h3>
        <p className="text-gray-600 mb-4">{description}</p>
        <a href={link} className="text-blue-600 hover:text-blue-800" target="_blank" rel="noopener noreferrer">
          View Project â†’
        </a>
      </div>
    </div>
  );
};

export default ProjectCard;

import React from 'react';
import ProjectCard from '../components/ProjectCard';

const Home = () => {
  const projects = [
    {
      title: "E-commerce Platform",
      description: "A full-stack e-commerce platform built with React and Node.js",
      image: "https://images.unsplash.com/photo-1661956602116-aa6865609028",
      link: "#"
    },
    {
      title: "Portfolio Website",
      description: "A responsive portfolio website using React and Tailwind CSS",
      image: "https://images.unsplash.com/photo-1667372393119-3d4c48d07fc9",
      link: "#"
    }
  ];

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
      <section className="mb-16">
        <h1 className="text-4xl font-bold mb-4">Hi, I'm John Doe</h1>
        <p className="text-xl text-gray-600">Full Stack Web Developer specializing in React and Node.js</p>
      </section>

      <section className="mb-16">
        <h2 className="text-2xl font-bold mb-6">My Projects</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {projects.map((project, index) => (
            <ProjectCard key={index} {...project} />
          ))}
        </div>
      </section>

      <section>
        <h2 className="text-2xl font-bold mb-6">Skills</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <div className="bg-white p-4 rounded-lg shadow text-center">React</div>
          <div className="bg-white p-4 rounded-lg shadow text-center">Node.js</div>
          <div className="bg-white p-4 rounded-lg shadow text-center">JavaScript</div>
          <div className="bg-white p-4 rounded-lg shadow text-center">Tailwind CSS</div>
        </div>
      </section>
    </div>
  );
};

export default Home;
