import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="bg-gray-900 p-4">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-white text-xl font-bold">MERN Dev</Link>

        <div className="hidden md:flex space-x-6">
          <Link to="/" className="text-white hover:text-indigo-400">Home</Link>
          <Link to="/projects" className="text-white hover:text-indigo-400">Projects</Link>
          <Link to="/skills" className="text-white hover:text-indigo-400">Skills</Link>
          <Link to="/contact" className="text-white hover:text-indigo-400">Contact</Link>
        </div>

        <button onClick={() => setIsOpen(!isOpen)} className="md:hidden text-white">
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
          </svg>
        </button>
      </div>

      {isOpen && (
        <div className="md:hidden">
          <div className="px-2 pt-2 pb-3 space-y-1">
            <Link to="/" className="block text-white hover:text-indigo-400 p-2">Home</Link>
            <Link to="/projects" className="block text-white hover:text-indigo-400 p-2">Projects</Link>
            <Link to="/skills" className="block text-white hover:text-indigo-400 p-2">Skills</Link>
            <Link to="/contact" className="block text-white hover:text-indigo-400 p-2">Contact</Link>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;

