        <Link data-curate-edit-id="f3617378-1202-40d2-9afa-2eb84da6bd2b" to="/" className="text-xl font-bold">MERN Developer</Link>

        <div data-curate-edit-id="f4c94e9b-2a43-4175-a8b1-1da525194b26" className="hidden md:flex space-x-4">
          <Link data-curate-edit-id="7cd40685-db17-4e16-bf54-4ed92d31ee85" to="/" className="hover:text-blue-400">Home</Link>
          <Link data-curate-edit-id="7b46c5fb-fdcc-4a9e-9d92-7c7ce30e5e06" to="/about" className="hover:text-blue-400">About</Link>
          <Link data-curate-edit-id="ae63beb6-0603-4cc9-aa3d-e22521151bbe" to="/skills" className="hover:text-blue-400">Skills</Link>
          <Link data-curate-edit-id="6c04abca-d156-45ef-bdbc-f34ac4a31ba8" to="/portfolio" className="hover:text-blue-400">Portfolio</Link>
