      </div>

      {isOpen && (
        <div data-curate-edit-id="18dda8e7-c83a-4e6c-b1d0-e8ffeaba04c7" className="md:hidden">
          <div data-curate-edit-id="bb581974-ff03-497c-9514-a0303d66473b" className="px-2 pt-2 pb-3 space-y-1">
            <Link data-curate-edit-id="e4469bca-368d-44ff-8537-4ceeeabe3ec2" to="/" className="block px-3 py-2 hover:bg-gray-700 rounded">Home</Link>
            <Link data-curate-edit-id="4ff69011-fd3a-41a4-bb3c-e56d2ff4e5c5" to="/about" className="block px-3 py-2 hover:bg-gray-700 rounded">About</Link>
            <Link data-curate-edit-id="7c738312-4c8e-4641-bb82-8e9c9a672f5c" to="/skills" className="block px-3 py-2 hover:bg-gray-700 rounded">Skills</Link>
            <Link data-curate-edit-id="dda2d1e2-1cfd-4e06-8108-75ad26ef15e0" to="/portfolio" className="block px-3 py-2 hover:bg-gray-700 rounded">Portfolio</Link>
            <Link data-curate-edit-id="18d0ab83-e6ca-4705-8519-2ec07909eb41" to="/contact" className="block px-3 py-2 hover:bg-gray-700 rounded">Contact</Link>
          </div>
        </div>
