  return (
    <footer data-curate-edit-id="9b9ef813-ba64-4d00-ae01-9c612ed8faea" className="bg-gray-800 text-white p-4 mt-auto">
      <div data-curate-edit-id="76de2210-4634-43d7-b2de-966fb2baca7f" className="container mx-auto text-center">
        <p data-curate-edit-id="b44585ce-9bea-4036-9e65-e330a04b00b4">&copy; 2024 MERN Stack Developer. All rights reserved.</p>
      </div>
    </footer>
  );
};

export default Footer;

import React from 'react';

const Home = () => {
  return (
    <div data-curate-edit-id="9df6e114-7d72-4d66-bf09-f3fbb2e43cf4" className="container mx-auto px-4 py-12">
      <div data-curate-edit-id="c8393034-898a-424c-94f8-c921f735d0f9" className="text-center">
        <h1 data-curate-edit-id="1870230c-ca56-4b06-bb1c-0b754dfea3e0" className="text-4xl font-bold mb-4">Welcome to My Portfolio</h1>
        <p data-curate-edit-id="1ddd237d-9faa-41b9-8053-e6086a6d1876" className="text-xl mb-8">MERN Stack Developer</p>
        <img data-curate-edit-id="9c6fc5d0-d562-4a2a-821f-ebc38fab4937" 
