export default Home;

import React from 'react';

const Skills = () => {
  const skills = [
    { name: "MongoDB", level: "90%" },
    { name: "Express.js", level: "85%" },
    { name: "React.js", level: "95%" },
    { name: "Node.js", level: "88%" }
  ];

  return (
    <div data-curate-edit-id="b61bdaa2-1d49-499c-8400-cbb6afee2b55" className="container mx-auto px-4 py-12">
      <h2 data-curate-edit-id="d8dcb3f1-e347-4dff-bb51-d7bb995a5eca" className="text-3xl font-bold mb-6">Skills</h2>
      <div data-curate-edit-id="e69bbcc9-5e68-4475-be8d-490ab6d616df" className="space-y-4">
        {skills.map((skill, index) => (
          <div data-curate-edit-id="599b7d98-9fbf-43b0-81f3-f3d7f8cdfe5f" key={index} className="mb-4">
            <div data-curate-edit-id="59ce7668-22a6-40cb-8558-addecbddee4a" className="flex justify-between mb-1">
              <span data-curate-edit-id="04e0fdbd-9a82-4d7c-bca5-818d037179d4" className="font-semibold">{skill.name}</span>
              <span data-curate-edit-id="c6d10938-ae0f-4db5-876f-0244f147bd94">{skill.level}</span>
            </div>
            <div data-curate-edit-id="d79927b9-8d74-42f3-87ab-2470a429c83a" className="w-full bg-gray-200 rounded-full h-2.5">
              <div data-curate-edit-id="2d48ae50-3019-4814-a888-88bb56ec31bd" 
                className="bg-blue-600 h-2.5 rounded-full" 
                style={{ width: skill.level }}
              ></div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Skills;

import React from 'react';

const Portfolio = () => {
  const projects = [
    {
      title: "E-commerce Platform",
      description: "Full-stack e-commerce website using MERN stack",
      image: "https://images.unsplash.com/photo-1557821552-17105176677c"
    },
    {
      title: "Task Management App",
      description: "Real-time task management application",
      image: "https://images.unsplash.com/photo-1559028012-481c04fa702d"
    }
  ];

  return (
    <div data-curate-edit-id="da14d0c0-3140-4396-935d-1c31987c7111" className="container mx-auto px-4 py-12">
      <h2 data-curate-edit-id="f245a7bf-1211-4705-b804-ed9cd0380081" className="text-3xl font-bold mb-6">Portfolio</h2>
      <div data-curate-edit-id="92a04877-efbb-4023-b109-ef75977fadd2" className="grid md:grid-cols-2 gap-6">
        {projects.map((project, index) => (
          <div data-curate-edit-id="a57337a5-9b2d-4314-8123-5cce27b37bfc" key={index} className="bg-white rounded-lg shadow-md overflow-hidden">
            <img data-curate-edit-id="34e87995-66cb-4037-85ac-9baca012ae55" 
              src={project.image}
              alt={project.title}
              className="w-full h-48 object-cover"
            />
            <div data-curate-edit-id="59419785-cfde-432c-b610-6ef572ef4fab" className="p-4">
              <h3 data-curate-edit-id="141653fa-3768-44b7-bed5-231b1931aa28" className="text-xl font-bold mb-2">{project.title}</h3>
              <p data-curate-edit-id="ca3a0b8e-7e85-4de1-90a3-ff75b6851240">{project.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Portfolio;

import React, { useState } from 'react';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Message sent successfully!');
    setFormData({ name: '', email: '', message: '' });
  };

  return (
    <div data-curate-edit-id="c421a3ae-6f59-4b18-b3f5-deef615f9636" className="container mx-auto px-4 py-12">
      <h2 data-curate-edit-id="4753e514-8574-471c-a1e6-5104a5b84520" className="text-3xl font-bold mb-6">Contact Me</h2>
      <form data-curate-edit-id="0822feb3-264c-476a-ad08-6773df244d1b" onSubmit={handleSubmit} className="max-w-lg mx-auto">
        <div data-curate-edit-id="679331b0-4742-4916-bcc7-cb52b87e0f43" className="mb-4">
          <label data-curate-edit-id="6b5b03ae-fbf2-4d88-84e0-3ea072ee4edf" htmlFor="name" className="block mb-2">Name</label>
          <input data-curate-edit-id="167c22c7-de66-4756-958f-b14fc53afdb3"
            type="text"
            id="name"
            value={formData.name}
            onChange={(e) => setFormData({...formData, name: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div data-curate-edit-id="31b11fce-6578-45a6-867c-0892bcccdde9" className="mb-4">
          <label data-curate-edit-id="d65e9f43-686a-4837-b05a-e304882e485e" htmlFor="email" className="block mb-2">Email</label>
          <input data-curate-edit-id="112d889a-f208-49f7-88a3-ccbf286c24e5"
            type="email"
            id="email"
            value={formData.email}
            onChange={(e) => setFormData({...formData, email: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div data-curate-edit-id="bfbb583a-fd3e-40cb-9c60-3a10bb53af50" className="mb-4">
          <label data-curate-edit-id="2862183c-7743-479a-b790-a768052f6226" htmlFor="message" className="block mb-2">Message</label>
          <textarea data-curate-edit-id="bcdf6b80-def5-4a76-ab03-e79cc12747a0"
            id="message"
            value={formData.message}
            onChange={(e) => setFormData({...formData, message: e.target.value})}
            className="w-full p-2 border rounded"
            rows="4"
            required
          ></textarea>
        </div>
        <button data-curate-edit-id="254202c1-db0f-41f7-82d8-24b9e3a8340b" type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          Send Message
        </button>
      </form>
    </div>
  );
};

export default Contact;
