import React, { useState } from 'react';

const CalorieTracker = () => {
  const [calories, setCalories] = useState([]);
  const [food, setFood] = useState('');
  const [amount, setAmount] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (food && amount) {
      setCalories([...calories, { food, amount }]);
      setFood('');
      setAmount('');
    }
  };

  return (
    <div data-curate-edit-id="56d96a6e-b31e-4102-bdea-85f20a925879" className="container mx-auto px-4 py-8">
      <h1 data-curate-edit-id="a729c325-c3cb-4e0a-84e8-eb943a6106a4" className="text-3xl font-bold mb-6">Calorie Tracker</h1>
      <form data-curate-edit-id="7da9f7ee-0162-439e-aa08-aee408c964f9" onSubmit={handleSubmit} className="mb-8">
        <div data-curate-edit-id="251c522a-b472-4655-a41a-7a60b8551afd" className="flex gap-4">
          <input data-curate-edit-id="24b149c7-02be-413c-a995-3e76cbce4e2a"
            type="text"
            value={food}
            onChange={(e) => setFood(e.target.value)}
            placeholder="Food item"
            className="border p-2 rounded"
          />
          <input data-curate-edit-id="b7d0190c-3070-49be-b955-ac198f299f72"
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            placeholder="Calories"
            className="border p-2 rounded"
          />
          <button data-curate-edit-id="4d1aec1e-2f07-4c23-a6b1-0bb79e5f335f" type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
            Add
          </button>
        </div>
      </form>
      <div data-curate-edit-id="27b71b44-3c4b-4ebf-9084-2384544df564">
        {calories.map((item, index) => (
          <div data-curate-edit-id="d7cb697c-953e-4d5a-9805-70ad1501fdc8" key={index} className="bg-gray-100 p-4 mb-2 rounded">
            {item.food}: {item.amount} calories
          </div>
        ))}
      </div>
    </div>
  );
};

export default CalorieTracker;

