import React from 'react';
  import ReactDOM from 'react-dom';
  import { BrowserRouter } from 'react-router-dom';
  import App from './App';
  import './index.css';

  ReactDOM.render(
    <BrowserRouter>
      <App />
    </BrowserRouter>,
    document.getElementById('root')
  );

import React from 'react';
  import { Routes, Route } from 'react-router-dom';
  import Navbar from './components/Navbar';
  import Footer from './components/Footer';
  import Home from './pages/Home';
  import About from './pages/About';
  import BlogPost from './pages/BlogPost';

  const App = () => {
    return (
      <div className='min-h-screen flex flex-col'>
        <Navbar />
        <main className='flex-grow'>
          <Routes>
            <Route path='/' element={<Home />} />
            <Route path='/about' element={<About />} />
            <Route path='/post/:id' element={<BlogPost />} />
          </Routes>
        </main>
        <Footer />
      </div>
    );
  };

  export default App;

import React, { useState } from 'react';
  import { Link } from 'react-router-dom';

  const Navbar = () => {
    const [isOpen, setIsOpen] = useState(false);

    return (
      <nav className='bg-white shadow-lg'>
        <div className='max-w-6xl mx-auto px-4'>
          <div className='flex justify-between items-center py-4'>
            <Link to='/' className='text-2xl font-bold text-gray-800'>Blog</Link>
            <div className='hidden md:flex space-x-4'>
              <Link to='/' className='text-gray-600 hover:text-gray-900'>Home</Link>
              <Link to='/about' className='text-gray-600 hover:text-gray-900'>About</Link>
            </div>
            <button onClick={() => setIsOpen(!isOpen)} className='md:hidden'>
              <svg className='w-6 h-6' fill='none' stroke='currentColor' viewBox='0 0 24 24'>
                <path strokeLinecap='round' strokeLinejoin='round' strokeWidth={2} d={isOpen ? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16M4 18h16'} />
              </svg>
            </button>
          </div>
          {isOpen && (
            <div className='md:hidden pb-4'>
              <Link to='/' className='block py-2 text-gray-600 hover:text-gray-900'>Home</Link>
              <Link to='/about' className='block py-2 text-gray-600 hover:text-gray-900'>About</Link>
            </div>
          )}
        </div>
      </nav>
    );
  };

  export default Navbar;

import React from 'react';

  const Footer = () => {
    return (
      <footer className='bg-gray-800 text-white py-8'>
        <div className='max-w-6xl mx-auto px-4'>
          <div className='flex flex-col md:flex-row justify-between items-center'>
            <div className='mb-4 md:mb-0'>
              <h3 className='text-xl font-bold'>Blog Website</h3>
              <p className='text-gray-400'>Share your thoughts with the world</p>
            </div>
            <div className='flex space-x-4'>
              <a href='#' className='hover:text-gray-400'>Twitter</a>
              <a href='#' className='hover:text-gray-400'>Facebook</a>
              <a href='#' className='hover:text-gray-400'>Instagram</a>
            </div>
          </div>
        </div>
      </footer>
    );
  };

  export default Footer;

import React from 'react';
  import { Link } from 'react-router-dom';

  const BlogCard = ({ post }) => {
    return (
      <div className='bg-white rounded-lg shadow-md overflow-hidden'>
        <img src={post.image} alt={post.title} className='w-full h-48 object-cover' />
        <div className='p-4'>
          <h2 className='text-xl font-semibold mb-2'>{post.title}</h2>
          <p className='text-gray-600 mb-4'>{post.excerpt}</p>
          <Link to={`/post/${post.id}`} className='text-blue-600 hover:text-blue-800'>
            Read more â†’
          </Link>
        </div>
      </div>
    );
  };

  export default BlogCard;

import React from 'react';
  import BlogCard from '../components/BlogCard';
  import { blogPosts } from '../data/posts';

  const Home = () => {
    return (
      <div className='max-w-6xl mx-auto px-4 py-8'>
        <h1 className='text-4xl font-bold mb-8'>Latest Posts</h1>
        <div className='grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6'>
          {blogPosts.map(post => (
            <BlogCard key={post.id} post={post} />
          ))}
        </div>
      </div>
    );
  };

  export default Home;

import React from 'react';

  const About = () => {
    return (
      <div className='max-w-4xl mx-auto px-4 py-8'>
        <h1 className='text-4xl font-bold mb-6'>About Us</h1>
        <div className='prose lg:prose-xl'>
          <p className='text-gray-600 mb-4'>
            Welcome to our blog! We are passionate about sharing knowledge and insights on various topics.
          </p>
          <p className='text-gray-600 mb-4'>
            Our team of writers and contributors work hard to bring you high-quality content that informs, entertains, and inspires.
          </p>
        </div>
      </div>
    );
  };

  export default About;

import React from 'react';
  import { useParams } from 'react-router-dom';
  import { blogPosts } from '../data/posts';

  const BlogPost = () => {
    const { id } = useParams();
    const post = blogPosts.find(post => post.id === parseInt(id));

    if (!post) return <div>Post not found</div>;

    return (
      <div className='max-w-4xl mx-auto px-4 py-8'>
        <h1 className='text-4xl font-bold mb-4'>{post.title}</h1>
        <img src={post.image} alt={post.title} className='w-full h-64 object-cover rounded-lg mb-6' />
        <div className='prose lg:prose-xl'>
          <p className='text-gray-600'>{post.content}</p>
        </div>
      </div>
    );
  };

  export default BlogPost;

export const blogPosts = [
    {
      id: 1,
      title: 'Getting Started with React',
      excerpt: 'Learn the basics of React and start building web applications.',
      content: 'React is a popular JavaScript library for building user interfaces...',
      image: 'https://images.unsplash.com/photo-1633356122544-f134324a6cee'
    },
    {
      id: 2,
      title: 'Web Design Trends 2024',
      excerpt: 'Explore the latest trends in web design and development.',
      content: 'The web design landscape is constantly evolving...',
      image: 'https://images.unsplash.com/photo-1581291518633-83b4ebd1d83e'
    },
    {
      id: 3,
      title: 'Productivity Tips',
      excerpt: 'Boost your productivity with these proven techniques.',
      content: 'In today\'s fast-paced world, productivity is key...',
      image: 'https://images.unsplash.com/photo-1483058712412-4245e9b90334'
    }
  ];
